import { useEffect, useState } from 'react';
import { fetchTodoList, fetchEditTodo, fetchSaveTodo } from '../services';

function TodoList() {

  const [list, setList] = useState([]);

  useEffect(() => {
    async function getList() {
      const result = await fetchTodoList();
      if (result) setList(result);
    }
    getList();
  }, []);


  // Fonte: https://developer.mozilla.org/pt-BR/docs/Web/JavaScript/Reference/Global_Objects/Array/sort
  // const sortedList = list.sort(function (a, b) {
  //   if (a.task > b.task) {
  //     return 1;
  //   }
  //   if (a.task < b.task) {
  //     return -1;
  //   }
  //   return 0;
  // });


  async function handleSave(formData) {
    await fetchSaveTodo(formData)
    window.location.reload()
  }

  async function handleRemove(id) {
    await fetchEditTodo(id);
    window.location.reload()
  }

  return(
    <div>
      <form>
        <span>Tarefa </span>
        <input type="text"></input>
        <span>Status </span>
        <select>
          <option>Pendente</option>
          <option>Em andamento</option>
          <option>Pronto</option>
        </select>
        <p>{FormData}</p>
        {/* <button
          type = 'submit'
          onClick = {() => handleSave(formData)}
        >salvar</button> */}
      </form>
      <ul>
         {list.map((todo, index) => 
          (<li key={index}> 
            {todo.task} 
            {todo.status} 
            {todo.createDate} 
            {/* <button>editar</button> */}
            <button
              type='submit'
              key = { index }
              onClick = {() => handleRemove(todo._id)}
            >excluir</button>
          </li>)
        )}
      </ul>

    </div>
  );
}

export default TodoList;
